    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        Bank bank;
        BankController saveBank;

        bank = new Bank();
        saveBank = new BankController();
       

        bank.setName(txtName.getText());
        bank.setId(txtId.getText());
        bank.setLocated(txtLocated.getText());
        bank.setDateOfFundation(txtDateOfFundation.getText());
        bank.setNumberOfCustomers(JTValidation(txtNumberOfCustomers.getText()));
        
        saveBank.CreateBank(bank);
        JOptionPane.showMessageDialog(null, "Bank add succesfull");
    }//GEN-LAST:event_btnRegisterActionPerformed

