    private void btnRegisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRegisterActionPerformed
        Bread FrmSell;
        int id;
        String name;
        String type;
        int units = 0;
        float price;  
        price = 0.0F;
        BakeryController bakeryController;
        bakeryController = new BakeryController();
        
        id = Integer.parseInt(txtid.getText());
        name = txtName.getText();
        type = cmbCustoerType.getSelectedItem().toString();

        FrmSell = new Bread(id, name, type, units, price);
        
        bakeryController.register(FrmSell, this);
        
        txtName.setText("");
        
    }//GEN-LAST:event_btnRegisterActionPerformed

    private void txtidKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtidKeyPressed
        valideIntegers(evt);
    }//GEN-LAST:event_txtidKeyPressed

    private void btnReturnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnReturnActionPerformed
        FrmBakery view = new FrmBakery();
        view.setVisible(true);
        this.setVisible(false);
    }//GEN-LAST:event_btnReturnActionPerformed

    private void valideIntegers(KeyEvent evt) {
        String value = txtid.getText();
        int lenght = value.length();
        if(evt.getKeyChar() >= '0' && evt.getKeyChar() <= '9'){
            txtid.setEditable(true);
            lblidError.setText("");
            if(lenght>=10){
                txtid.setEditable(false);
                lblidError.setText("Max 10 digits");
            }
        }else{
            txtid.setEditable(false);
            lblidError.setText("*Only digits (0-9)");
        }
    }

